import React from 'react';
import './App.css';

import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

import UserRouter from './components/UserPanel/UserRouter';
import Player from './components/Player';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFrequence: 1,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.setState({ currentFrequence: currentFrequence })
  }
  render() {
    return (
      <main className="main">
        <Visualizer></Visualizer>
        <MediaPlayer soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </MediaPlayer>
      </main>
    )
  }
}

class Visualizer extends React.Component {
  constructor(props) {
    super(props)
  }
  render() {
    return (
      <section className="visualizer">
        <img id="sky-image" src={process.env.PUBLIC_URL + '/sky.jpg'}></img>
      </section>
    )
  }
}

class MediaPlayer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSongID: null,
      open: true,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
    this.updateCurrentSong = this.updateCurrentSong.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.props.soundTrackPlayerCallback(currentFrequence);
  }

  updateCurrentSong(songID) {
    this.setState({
      currentSongID: songID,
    })
  }

  render() {
    return (
      <section className="root">

        <div className={this.state.open ? "slider-show" : "slider-hide"}>
          <div className="button">
            {this.state.open
              ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
              : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
            }
          </div>
          <UserRouter updateCurrentSong={this.updateCurrentSong}></UserRouter>
          <Player
            currentSongID={this.state.currentSongID}
            soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}
          >
          </Player>
        </div>
        </section>)
  }
}

export default App;
