import React from 'react';
import './Player.css';
import { API_BASE_URL } from '../../constants/apiConstants'

//https://developer.mozilla.org/en-US/docs/Web/API/AnalyserNode/getFloatFrequencyData
class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.analyserNode = null;
        this.dataArray = null;
        this.bufferLength = null;
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
        this.onAudioLoad = this.onAudioLoad.bind(this);
    }

    componentDidMount() {
        let canvas = document.querySelector("visualizer");
        let ctx = canvas.getContext("2d");

        canvas.width = document.body.clientWidth;
        canvas.height = document.body.clientHeight;

        let centerX = canvas.width / 2;
        let centerY = canvas.height / 2;
        let radius = document.body.clientWidth <= 425 ? 120 : 160;
        let steps = document.body.clientWidth <= 425 ? 60 : 120;
        let interval = 360 / steps;
        let pointsUp = [];
        let pointsDown = [];
        let running = false;
        let pCircle = 2 * Math.PI * radius;
        let angleExtra = 90;

        // Create points
        for (let angle = 0; angle < 360; angle += interval) {
            let distUp = 1.1;
            let distDown = 0.9;

            pointsUp.push({
                angle: angle + angleExtra,
                x: centerX + radius * Math.cos((-angle + angleExtra) * Math.PI / 180) * distUp,
                y: centerY + radius * Math.sin((-angle + angleExtra) * Math.PI / 180) * distUp,
                dist: distUp
            });

            pointsDown.push({
                angle: angle + angleExtra + 5,
                x: centerX + radius * Math.cos((-angle + angleExtra + 5) * Math.PI / 180) * distDown,
                y: centerY + radius * Math.sin((-angle + angleExtra + 5) * Math.PI / 180) * distDown,
                dist: distDown
            });
        }
        this.onAudioLoad();
        this.onTimeUpdateHandler();
    }

    onAudioLoad = () => {
        try {
            const audioCtx = new AudioContext();
            var audio = document.getElementById('track');
            const audioSourceNode = audioCtx.createMediaElementSource(audio);

            this.analyserNode = audioCtx.createAnalyser();
            this.analyserNode.fftSize = 1024;
            this.bufferLength = this.analyserNode.frequencyBinCount;
            this.dataArray = new Float32Array(this.bufferLength);

            audioSourceNode.connect(this.analyserNode);
            this.analyserNode.connect(audioCtx.destination);
        } catch (error) {
        }
    }

    onTimeUpdateHandler = () => {
        try {
            //Schedule next redraw
            requestAnimationFrame(this.onTimeUpdateHandler);

            //Get spectrum data
            this.analyserNode.getFloatFrequencyData(this.dataArray);

            //Draw black background
            this.canvasCtx.fillStyle = 'rgb(255, 255, 255)';
            this.canvasCtx.fillRect(0, 0, this.canvas.width, this.canvas.height);

            //Draw spectrum
            const barWidth = (this.canvas.width / this.bufferLength) * 2.5;
            let posX = 0;
            for (let i = 0; i < this.bufferLength; i++) {
                const barHeight = (this.dataArray[i] + 140) * 2;
                this.canvasCtx.fillStyle = 'rgb(52 , 58, 74)';
                this.canvasCtx.fillRect(posX, this.canvas.height - barHeight / 2, barWidth, barHeight / 2);
                posX += barWidth + 1;
            }
        } catch (error) {
        }
    }

    render() {
        if (this.props.currentSongID) {
            return (
                <div className="player">
                    <figcaption id="caption">{this.props.currentSongName}</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src={API_BASE_URL + '/media/getSongFile?songID=' + this.props.currentSongID}
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        } else {
            return (
                <div className="player">
                    <figcaption id="caption">Test Song</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src="https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3"
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        }
    }
}

export default Player;
