import React from 'react';
import ListGroup from 'react-bootstrap/ListGroup';

import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import UploadDialog from '../UploadDialog';
import Song from './Song';
import cloneDeep from 'lodash/cloneDeep';


class Playlist extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            songlist: cloneDeep(this.props.songCollection),
        }
    }

    render() {
        return (
            <div>
                <p>{this.props.playlistName}</p>
                <UploadDialog songlist={this.state.songlist}>
                </UploadDialog>

                <Accordion>
                    <Card>
                        <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                {this.props.playlistName}
                            </Accordion.Toggle>
                        </Card.Header>
                        {this.state.songlist.map((song, index) => {
                            return (
                                <Accordion.Collapse eventKey="0">
                                    <Card.Body key={index}><Song songName={song.songName} songUrl={song.songUrl}></Song></Card.Body>
                                </Accordion.Collapse>
                            )
                        })}
                    </Card>
                </Accordion>

            </div>
        )
    }
}

export default Playlist;
