import React from 'react';
import './App.css';

import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

import UserRouter from './components/UserPanel/UserRouter';
import Player from './components/Player';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFrequence: 1,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.setState({ currentFrequence: currentFrequence })
  }
  render() {
    return (
      <div className="main">
        <div className="visualizer">
          <Visualizer></Visualizer>
        </div>
        <div className="root">
          <MediaPlayer
            soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
          </MediaPlayer>
        </div>
      </div>
    )
  }
}

class Visualizer extends React.Component {
  constructor(props) {
    super(props)
  }
  //       <img src={process.env.PUBLIC_URL + '/sky.jpg'}></img>
  render() {
    return (
      <div>
      </div>
    )
  }
}

class MediaPlayer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSongID: null,
      open: true,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
    this.updateCurrentSong = this.updateCurrentSong.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.props.soundTrackPlayerCallback(currentFrequence);
  }

  updateCurrentSong(songID) {
    this.setState({
      currentSongID: songID,
    })
  }

  render() {
    return (
      <div>

        <div className={this.state.open ? "slider-show" : "slider-hide"}>
          <div className="button">
            {this.state.open ?
              <svg 
              xmlns="http://www.w3.org/2000/svg" 
              width="16" 
              height="16" 
              fill="currentColor" 
              className="bi bi-arrow-right-square" 
              viewBox="0 0 16 16"
              onClick={() => { this.setState({ open: !this.state.open }); }}
              >
                <path fill-rule="evenodd" d="M15 2a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h12a1 1 0 0 0 1-1V2zM0 2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V2zm4.5 5.5a.5.5 0 0 0 0 1h5.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3a.5.5 0 0 0 0-.708l-3-3a.5.5 0 1 0-.708.708L10.293 7.5H4.5z" />
              </svg>
              : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
            }
          </div>

          <UserRouter updateCurrentSong={this.updateCurrentSong}></UserRouter>
          <Player
            currentSongID={this.state.currentSongID}
            soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}
          >
          </Player>
        </div>
      </div>)
  }
}

export default App;
