import React from 'react';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import { PinDropSharp } from '@material-ui/icons';

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
   render(){ 
    const playLists = {name: this.props.collection, isOpen: false};
     return (
     <MusicPlayerView playLists = {playLists}>
     </MusicPlayerView>
     )
   }
}


function MusicPlayerView(props) {
  const [isOpenList, setOpen] = React.useState(true);

  const handleClick = (i) => {
    setOpen(!props.playLists.isOpen[i]);
  };

  const playLists = props.playLists.name.map((playlist, index) => {
    return (
      <li key = {index}>
        <ListItem button onClick={handleClick}>
        <ListItemIcon>
          <DoubleArrowIcon />
        </ListItemIcon>
        <ListItemText primary= {playlist} />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      </li>
    )
  });

  return (
    <div>
    <ol>{playLists}</ol></div>

  );
}

export default MusicCollection
