import React from 'react';

import './App.css';
import drawAudio from './components/AudioAnalyzer.js';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import DialogTitle from '@material-ui/core/DialogTitle';
import injectSheet, { ThemeProvider } from 'react-jss';
import Dialog from '@material-ui/core/Dialog';
import RegistrationForm from './components/Dialogs/RegistrationForm';
import AlertComponent from './components/AlertComponent';
import LoginForm from './components/Dialogs/LoginForm';
import PrivateRoute from './utils/PrivateRoute';
import MusicCollection from './components/MusicCollection/MusicCollection';
import UserMenu from './components/UserMenu';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";

const styles = () => ({
  wrapper: {
    opacity: props => props.opacity,
    backgroundImage: "url('https://nypost.com/wp-content/uploads/sites/2/2019/11/universe-in-giant-loop.jpg?quality=90&strip=all&w=1286')",
    height: 700,
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
  }
})

const Comp = ({ classes }) => (
  <div className={classes.wrapper}>
  </div>
)

const StyledComp = injectSheet(styles)(Comp)
const theme = {
  background: "yellow",
  color: '#24292e'
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFrequence: 1,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.setState({ currentFrequence: currentFrequence })
  }
  render() {
    console.log(this.state.currentFrequence)
    return (
      <div>
        <ThemeProvider theme={theme}>
          <StyledComp opacity={this.state.currentFrequence} />
        </ThemeProvider>
        <MusicPlayerView soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </MusicPlayerView>
      </div>
    )
  }
}

class MusicPlayerView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collection: [],
      currentSong: '',
      currentUrl: '',
      open: true,
      isDialogOpen: false,
      showError: '',
      errorMessage: null,
      title: 'Register',
      isLoggedIn: false,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.props.soundTrackPlayerCallback(currentFrequence);
  }

  handleRegistrationFormCallback = (errorMessage) => {
    this.setState({ errorMessage: errorMessage })
  }

  handleCallbackFromUserMenu = () => {
    this.setState({ isDialogOpen: open });
  }

  handleClose = () => {
    this.setState({ isDialogOpen: false });
  };

  updateTitle = (title) => {
    this.setState({ title: title });
  }

  render() {
    return (
      <div>
        <div className="root">
          <div className={this.state.open ? "slider-show" : "slider-hide"}>
          <UserMenu parentCallback = {this.handleCallbackFromUserMenu}></UserMenu>
            <Router>
              <div className="App">
                <div className="container d-flex align-items-center flex-column">
                  <Switch>
                    <Route path="/" exact={true}>
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>
                    <Route path="/register">
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>
                    <Route path="/login">
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <LoginForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>
                    <PrivateRoute path="/loggedIn">
                      <MusicLibrary
                        collection={this.state.collection}
                        currentSong={this.state.currentSong}
                        currentUrl={this.state.currentUrl}
                        >
                      </MusicLibrary>
                    </PrivateRoute>
                  </Switch>
                  <AlertComponent errorMessage={this.state.errorMessage} hideError={() => {
                    this.setState({ errorMessage: null });
                  }} />
                </div>
              </div>
              <div className="button">
                {this.state.open
                  ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
                  : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
                }
              </div>
            </Router>
          </div>
        </div>

        <SoundTrackPlayer
          currentSong={this.state.currentSong}
          currentUrl={this.state.currentUrl}
          soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </SoundTrackPlayer>
      </div>)
  }
}

class MusicLibrary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playlistCollection: [],
      value: '',
    }
  }

  render() {
    return (
        <div className="MusicLibrary">
          <div >
          <MusicCollection playlistCollection={this.state.playlistCollection}></MusicCollection>
          </div>
        </div>
    )

  }
}

class SoundTrackPlayer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
    }
    this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    this.fetchFrequency = this.fetchFrequency.bind(this);
  }

  fetchFrequency = async (value, currentTimeMs) => {
    var myvalue = await value;
    console.log('value: ' + myvalue[Math.round(currentTimeMs)]);
    this.props.soundTrackPlayerCallback(myvalue[Math.round(currentTimeMs)]);
  }

  original = Promise.resolve(drawAudio('https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3'));
  onTimeUpdateHandler = () => {
    const cast = Promise.resolve(this.original);
    var audio = document.getElementById('track');
    var currentTimeMs = audio.currentTime * 10;
    this.setState({ time: Math.round(currentTimeMs) })

    console.log(Math.round(currentTimeMs));
    cast.then((value) => this.fetchFrequency(value, currentTimeMs));
  }

  render() {
    return (
      <div className="Player">
        <div className={this.state.open ? "player-show" : "player-hide"}>
          <figcaption>{this.props.currentSong}</figcaption>
          <audio
            id="track"
            controls
            src={this.props.currentUrl}
            onTimeUpdate={(e) => this.onTimeUpdateHandler(e)}
          >
            Your browser does not support the
          <code>audio</code> element.
      </audio>
          <div className="button2">
            {this.state.open
              ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
              : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
            }
          </div>
        </div>
      </div>
    )
  }
}

export default App;
