import React, { useState } from 'react';

import axios from 'axios';
import { API_BASE_URL, ACCESS_TOKEN_NAME } from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

import useAlert from 'react-alert';

function LoginForm(props) {
    const [state, setState] = useState({
        email: "",
        password: "",
        successMessage: null,
        errorMessage: null,
    })

    const alert = useAlert();

    const handleChange = (e) => {
        const { id, value } = e.target
        setState(prevState => ({
            ...prevState,
            [id]: value
        }))
    }

    const loginPost = async (payload) => {
        return axios.post(API_BASE_URL + '/user/login', payload)
            .then(response => {
                if (response.status < 400) {
                    setState(prevState => ({
                        ...prevState,
                        'successMessage': 'Login successful. Redirecting to home page..'
                    }))
                    localStorage.setItem(ACCESS_TOKEN_NAME, response.data);
                    redirectToHome();
                }
                else {
                    setState(prevState => ({
                        ...prevState,
                        'errorMessage': response.data,
                    }))
                }
            })
            .catch(error => {
                if (!error.response) {
                    alert('NETWORK ERROR');
                } else {
                    setState(prevState => ({
                        ...prevState,
                        'errorMessage': error.response.data
                    }))
                    alert.show('Oh look, an alert!')
                }
            });
    }

    const handleSubmitClick = (e) => {
        e.preventDefault();
        const payload = {
            "email": state.email,
            "password": state.password,
        }

        Promise.resolve(loginPost(payload)).catch(error => console.log(error));
    }

    const redirectToHome = () => {
        props.history.push('/');
    }
    const redirectToRegister = () => {
        props.history.push('/register');
    }
    return (
        <Modal show={true} onHide={() => redirectToHome()} animation={false}>
            <Modal.Header closeButton>
                Login
                </Modal.Header>
            <Modal.Body>
                <form>
                    <div className="form-group text-left">
                        <label >Email address</label>
                        <input type="email"
                            className="form-control"
                            id="email"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            value={state.email}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-group text-left">
                        <label>Password</label>
                        <input type="password"
                            className="form-control"
                            id="password"
                            placeholder="Password"
                            value={state.password}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-check">
                    </div>
                </form>
                <Button
                    type="submit"
                    className="btn btn-primary"
                    onClick={handleSubmitClick}
                >Submit</Button>
            </Modal.Body>
            <Modal.Footer>
                <div className="registerMessage">
                    <span>Dont have an account? </span>
                    <span className="loginText" onClick={() => redirectToRegister()}>Register</span>
                </div>
                <Alert key={idx} variant={variant}>
                    This is a {variant} alertâ€”check it out!
                </Alert>
                <div className="alertMessage" style={{ display: state.errorMessage ? 'block' : 'none' }} role="alert">
                    <p>{state.errorMessage}</p>
                </div>
            </Modal.Footer>
        </Modal>
    )
}

export default withRouter(LoginForm);
