import React from 'react';
import MusicCollection from './components/MusicCollection.js';
import UploadDialog from './components/UploadDialog.js';
import './App.css';

 class App extends React.Component {
   render(){ 
     return (
     <MusicPlayerView>
     </MusicPlayerView>
     )
   }
}

class MusicPlayerView extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      collection: Array(),
      currentSong: '',
      currentUrl: '',
    }
  }

  handleCallback = (childData) =>{
    this.setState({collection: childData});
  }

  handleCallback2 = (childData) =>{
    this.setState({currentUrl: childData});
  }

  render(){
    return (
    <div>
      <div className = "Library">
      <MusicLibrary 
      collection = {this.state.collection} 
      currentSong = {this.state.currentSong} 
      currentUrl = {this.state.currentUrl} 
      parentCallback = {this.handleCallback} 
      parentCallback2 = {this.handleCallback2}></MusicLibrary>
      </div>
      <SoundTrackPlayer currentSong = {this.state.currentSong} currentUrl = {this.state.currentUrl}></SoundTrackPlayer>
      <ControlPanel></ControlPanel>
      <UploadView></UploadView>
    </div> )
  }
}

class MusicLibrary extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      value: '',
      myCollection: this.props.collection,
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCallback = this.handleCallback.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    const playlists = this.props.collection.slice();
    playlists.push({name: this.state.value, songs: []});
    this.setState({value: ''});
    this.props.parentCallback(playlists);
    event.preventDefault();
  }

  handleCallback = (childData) =>{
    this.setState({myCollection: childData});
  }
  handleCallback2 = (childData) =>{
    this.props.parentCallback2(childData);
  }

  render(){
    const myCollection = this.props.collection;
    return (
      <div>
      <div><UploadDialog collection = {myCollection} parentCallback = {this.handleCallback}></UploadDialog></div>
      <div><MusicCollection collection = {myCollection} parentCallback = {this.handleCallback2}></MusicCollection></div>
      <form onSubmit={this.handleSubmit}>
      <h2> Create a new playlist </h2>
        <label>
          name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>
    )

  }
}

class SoundTrackPlayer extends React.Component{
  render(){
    return (
      <div>
      <figcaption>{this.props.currentSong}</figcaption>
      <audio
      controls
      src={this.props.currentUrl}>
          Your browser does not support the
          <code>audio</code> element.
      </audio>
      </div>
  )
  }
}

class ControlPanel extends React.Component{
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component{
  render() {
    return <div>UploadView</div>
  }
}


export default App;
