import React from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Playlist from './Playlist';

class MusicCollection extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            playlistName: "",
            songCollection: [],
        }
        this.handlePlaylistNameChange = this.handlePlaylistNameChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleDialogOpen = this.handleDialogOpen.bind(this);
        this.handleDialogClose = this.handleDialogClose.bind(this);
    }

    handlePlaylistNameChange(event) {
        this.setState({ playlistName: event.target.value });
    }

    handleSubmit(event) {
        event.preventDefault();
        this.props.playlistCollection.push(this.state.playlistName);
        this.setState({ playlistName: "" });
    }

    handleDialogOpen(){
        this.setState({open: true})
    }

    handleDialogClose(){
        this.setState({open: false})
    }

    render() {
        console.log(this.props.playlistCollection)
        return (
            <div>
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="26"
                    height="26"
                    fill="currentColor"
                    className="bi bi-plus"
                    viewBox="0 0 16 16"
                    onClick={this.handleDialogOpen}
                >
                    <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z" />
                </svg>
                <Dialog
                    open={this.state.open}
                    onClose={this.handleDialogClose}>
                    <DialogTitle>Add a playlist</DialogTitle>
                    <DialogContent>
                        <TextField
                            value={this.state.songName}
                            onChange={this.handleNameChange}
                            autoFocus
                            margin="dense"
                            label="Song name"
                            fullWidth
                        />
                    </DialogContent>

                    <DialogActions>
                        <Button onClick={this.handleDialogClose} color="primary">
                            Close
          </Button>
                        <Button onClick={this.handleSubmit} color="primary">
                            Submit
          </Button>
                    </DialogActions>
                </Dialog>


                <form onSubmit={this.handleSubmit} className="Form">
                    <p>Create a new playlist</p>
                    <label>
                        name:
          <input type="text" value={this.state.playlistName} onChange={this.handlePlaylistNameChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                <ListGroup>
                    {this.props.playlistCollection.map((playlist, index) => {
                        return (
                            <ListGroup.Item key={index}>
                                <Playlist playlistName={playlist} songCollection={this.state.songCollection}></Playlist>
                            </ListGroup.Item>
                        )
                    })}
                </ListGroup>
            </div>
        )
    }
}

export default MusicCollection;
