import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Select from 'react-select'
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  form: {
    display: 'flex',
    flexDirection: 'column',
    margin: 'auto',
    width: 'fit-content',
  },
  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 120,
  },
  formControlLabel: {
    marginTop: theme.spacing(1),
  },
}));

class UploadDialog extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      open: false,
      value: '',
      myurl: '',
      playlist: '',
    };
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleUrlChange = this.handleUrlChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleNameChange(event){
    this.setState({value: event.target.value});
  };

  handleUrlChange(event){
    this.setState({myurl: event.target.value});
  };

  handleChange(e) {
    this.setState({ playlist: e.value });
  }

  handleClickOpen = () => {
    this.setState({open: true});
  };

  handleClose = () => {
    this.setState({open: false});
    this.setState({value: ''});
    this.setState({myurl: ''});
  };

  handleSubmit = () => {
    this.setState({open: false});
    let mycollection = this.props.collection.slice();
    mycollection[this.state.playlist].songs.push({songName: this.state.value, songUrl: this.state.myurl});
    this.props.parentCallback(mycollection);
    this.setState({value: ''});
  };

  playlists = this.props.collection.map((playlist, index) => {
    return(
    {label: playlist.name, value: index}
    )
  });

  render(){
  return (
    <React.Fragment>
      <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
        Upload your song
      </Button>
      <Dialog
        open={this.state.open}
        onClose={this.handleClose}
      >
        <DialogTitle>Upload a Song</DialogTitle>
        <DialogContent>
          <DialogContentText>
            You can choose the playlist. If you don't have a playlist yet, then go back to the page and create one.
          </DialogContentText>

          <Select 
          defaultValue = ''
          options={playlists} 
          className="basic-single"
          classNamePrefix="select"
          onChange={(e) => {this.handleChange(e)}}
          />

          <DialogContentText>
            You can now add a song to your playlist. Give your song a name and then add the url link to your song.
          </DialogContentText>

          <TextField 
            value={this.state.value} 
            onChange={this.handleNameChange}
            autoFocus
            margin="dense"
            label="Song name"
            fullWidth
          />
          <TextField
            value={this.state.myurl} 
            onChange={this.handleUrlChange}
            autoFocus
            margin="dense"
            label="Song URL"
            fullWidth
          />
        </DialogContent>

        <DialogActions>
          <Button onClick={this.handleClose} color="primary">
            Close
          </Button>
          <Button onClick={this.handleSubmit} color="primary">
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
}

export default UploadDialog
