import React from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route
} from "react-router-dom";

import PrivateRoute from '../../../utils/PrivateRoute';

import MusicCollection from '../../MusicCollection/MusicCollection';
import RegistrationForm from '../../Dialogs/RegistrationForm';
import LoginForm from '../../Dialogs/LoginForm';
import Header from '../Header/Header';
import Player from '../Player';
import { CURRENT_SONG_NAME, CURRENT_SONG_ID } from '../../../constants/apiConstants';

class MediaPlayer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentSongName: null,
            currentSongID: null,
        }
        this.updateCurrentSong = this.updateCurrentSong.bind(this);
    }

    updateCurrentSong(songName, songID) {
        this.setState({
            currentSongName: songName,
            currentSongID: songID,
        })
        sessionStorage.setItem(CURRENT_SONG_NAME, songName);
        sessionStorage.setItem(CURRENT_SONG_ID, songID);
    }

    render() {
        var currentSongName;
        var currentSongID;
        if(this.state.currentSongID){
            currentSongName = this.state.currentSongName;
            currentSongID = this.state.currentSongID
        } else if (sessionStorage.getItem(currentSongID))
        {
            currentSongName = sessionStorage.getItem(CURRENT_SONG_NAME);
            currentSongID = sessionStorage.getItem(currentSongID);
        }
        return (
            <Router>
                <div>
                    <Header/>
                    <Switch>
                        <Route path="/register">
                            <RegistrationForm />
                        </Route>
                        <Route path="/login">
                            <LoginForm />
                        </Route>
                        <PrivateRoute path="/">
                            <MusicCollection
                                updateCurrentSong={this.updateCurrentSong}
                            ></MusicCollection>
                            <Player
                                currentSongName={currentSongName}
                                currentSongID={currentSongID}
                                changedFrequencyCallback={this.props.setCurrentFrequency}
                            >
                            </Player>
                        </PrivateRoute>
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default MediaPlayer;
