import React from 'react';

import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';

class CreatePlaylistDialog extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            playlistName: "",
        };
        this.handleDialogOpen = this.handleDialogOpen.bind(this);
        this.handleDialogClose = this.handleDialogClose.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDialogOpen() {
        this.setState({ open: true })
    }

    handleDialogClose() {
        this.setState({
            open: false,
            playlistName: "",
        })
    }

    handleSubmit(event) {
        this.props.parentCallback(this.state.playlistName);
        this.setState({
            playlistName: "",
            open: false,
        });
        event.preventDefault();
    }

    handleNameChange(event) {
        this.setState({ playlistName: event.target.value })
    }

    render() {
        return (
            <div>
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="26"
                    height="26"
                    fill="currentColor"
                    className="bi bi-plus"
                    viewBox="0 0 16 16"
                    onClick={this.handleDialogOpen}
                >
                    <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z" />
                </svg>


                <Modal show={this.state.open} onHide={() => handleDialogClose} animation={false}>
                <Modal.Header closeButton>
                Add a playlist
                </Modal.Header>
                <Modal.Body>
                    <form>
                        <div className="form-group text-left">
                            <label >Playlist</label>
                            <input type="playlist"
                                className="form-control"
                                id="playlist"
                                aria-describedby="playlist"
                                placeholder="Enter playlist name"
                                value={this.state.playlistName}
                                onChange={this.handleNameChange}
                            />
                        </div>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                <Button
                        type="submit"
                        className="btn btn-primary"
                        onClick={handleSubmit}
                    >Submit</Button>
                </Modal.Footer>
            </Modal>


                <Dialog
                    open={this.state.open}
                    onClose={this.handleDialogClose}>
                    <DialogTitle>Add a playlist</DialogTitle>
                    <DialogContent>
                        <TextField
                            value={this.state.playlistName}
                            onChange={this.handleNameChange}
                            autoFocus
                            margin="dense"
                            label="Song name"
                            fullWidth
                        />
                    </DialogContent>

                    <DialogActions>
                        <Button onClick={this.handleDialogClose} color="primary">
                            Close
                     </Button>
                        <Button onClick={this.handleSubmit} color="primary">
                            Submit
                         </Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }
}

export default CreatePlaylistDialog;
