import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import styles from './MusicCollection.css'; 

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
   render(){ 
    const playLists = {name: this.props.collection, isOpen: false};
     return (
     <MusicPlayerView playLists = {playLists}>
     </MusicPlayerView>
     )
   }
}


function MusicPlayerView(props) {
  const [items, setItems] = useState([]);
  const [isOpen, setOpen] = useState(false);

  const addItem = event => {
    event.preventDefault();
    const tmpItems = items;
    setOpen(!isOpen);
    tmpItems[1] = isOpen;
    setItems(tmpItems);
  };

  const playLists = props.playLists.name.map((playlist, index) => {
    return (
      <li key = {index}>
        <ListItem button onClick={addItem}>
        <ListItemIcon>
          <DoubleArrowIcon />
        </ListItemIcon>
        <ListItemText primary= {playlist} />
        {items[index] ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={items[index]} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button onClick={() => {
            console.log('button pressed');
          }}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItem>
        </List>
      </Collapse>
      </li>
    )
  });

  return (
    <div>
    <ol>{playLists}</ol></div>

  );
}

export default MusicCollection
