import React from 'react';

import {
    BrowserRouter as Router,
    Switch,
    Route
} from "react-router-dom";

import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';

import PrivateRoute from '../../utils/PrivateRoute';

import MusicCollection from '../MusicCollection/MusicCollection';
import RegistrationForm from '../Dialogs/RegistrationForm';
import AlertComponent from '../AlertComponent';
import LoginForm from '../Dialogs/LoginForm';

class UserLounge extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playlistCollection: [],
            title: 'Register',
            errorMessage: null,
            showError: '',
        }
        this.handleClose = this.handleClose.bind(this);
    }

    handleRegistrationFormCallback = (errorMessage) => {
        this.setState({ errorMessage: errorMessage })
    }

    handleClose = () => {
        this.props.parentCallback();
    };

    updateTitle = (title) => {
        this.setState({ title: title });
    }

    render() {
        console.log('we are here')
        return (
            <div>
                        <div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>


            <Router>
                <div className="container d-flex align-items-center flex-column">
                    <Switch>
                        <Route path="/" exact={true}>
                            <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="false">
                                <div className="modal-dialog" role="document">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title" id="exampleModalLabel">New message</h5>
                                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                                <span aria-hidden="true">&times;</span>
                                            </button>
                                        </div>
                                        <div className="modal-body">
                                        <h1>jjjij</h1>
                                            <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                            <button type="button" className="btn btn-primary">Send message</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Route>
                        <Route path="/register">
                            <Dialog
                                open={this.props.isDialogOpen}
                                onClose={this.handleClose}>
                                <DialogTitle>{this.state.title}</DialogTitle>
                                <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                            </Dialog>
                        </Route>
                        <Route path="/login">
                            <Dialog
                                open={this.props.isDialogOpen}
                                onClose={this.handleClose}>
                                <DialogTitle>{this.state.title}</DialogTitle>
                                <LoginForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                            </Dialog>
                        </Route>
                        <PrivateRoute path="/loggedIn">
                            <MusicCollection playlistCollection={this.state.playlistCollection}></MusicCollection>
                        </PrivateRoute>
                    </Switch>
                    <AlertComponent errorMessage={this.state.errorMessage} hideError={() => {
                        this.setState({ errorMessage: null });
                    }} />
                </div>
            </Router>


            </div>
        )
    }
}

export default UserLounge;
