import React from 'react';
import drawAudio from './AudioAnalyzer.js';
import { API_BASE_URL, ACCESS_TOKEN_NAME } from '../constants/apiConstants';
import axios from 'axios';

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    }

    componentDidMount() {
        this.audioData = Promise.resolve(drawAudio(null));
    }

    onTimeUpdateHandler = () => {
        // get current timestamp from audio
        var audio = document.getElementById('track');
        var timestamp = audio.currentTime * 10;

        // resolve the current frequence and update parent
        this.audioData.then((value) => this.props.soundTrackPlayerCallback(value[Math.round(timestamp)]));
    }

    async getCurrentSong(currentSongID) {
        console.log('songID is: ' + currentSongID);
        return axios.get(API_BASE_URL + '/media/getSongFile', {params: {songID: currentSongID}}, { headers: { "auth-token": localStorage.getItem(ACCESS_TOKEN_NAME) } })
            .then(function (response) {
                if (response.status === 200) {
                    return response.data;
                }
            }
            )
            .catch(function (error) {
                console.log(error);
            })
    }

    handleSongPlay(){
        return Promise.resolve(this.getCurrentSong(this.props.currentSongID)).then(result => {
            return window.URL.createObjectURL(new Blob(result, { type: 'audio/mp3' }));});
    }

    render() {
        console.log(API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID);
        return (
            <div className="Player">
                <figcaption>{this.props.currentSongID}</figcaption>
                <audio
                    id="track"
                    controls
                    src={API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID}
                    onTimeUpdate={(e) => this.onTimeUpdateHandler(e)}
                >
                    Your browser does not support the
              <code>audio</code> element.
          </audio>
            </div>
        )
    }
}

export default Player;
