import React from 'react';
import './Player.css';
import { API_BASE_URL } from '../../constants/apiConstants';

window.AudioContext = window.AudioContext || window.webkitAudioContext;
const audioContext = new AudioContext();

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.update = this.update.bind(this);
        this.oncanplay = this.oncanplay.bind(this);
    }

    componentDidMount() {
        var audio = document.getElementById('track');
        audio.oncanplay(() => {
            var context = new AudioContext();
            var analyser = context.createAnalyser();
            analyser.fftSize = 64;
            var frequencyData = new Uint8Array(analyser.frequencyBinCount);
            var source = context.createMediaElementSource(this);
            source.connect(analyser);
            analyser.connect(context.destination);
            console.log(analyser.getByteFrequencyData(frequencyData));
            this.update();
        })
    }

    update() {
        requestAnimationFrame(this.update);

        console.log(this.analyser.getByteFrequencyData(this.frequencyData));
    };

    //https://dl.espressif.com/dl/audio/ff-16b-2c-44100hz.mp3
    //                        src={API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID}
    render() {
        if (this.props.currentSongID) {
            return (
                <div className="player">
                    <figcaption id="caption">{this.props.currentSongName}</figcaption>
                    <audio
                        id="track"
                        controls
                        src="https://dl.espressif.com/dl/audio/ff-16b-2c-44100hz.mp3"
                        onTimeUpdate={(e) => this.onTimeUpdateHandler}
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        } else return (
            <audio
            id="track"
            controls
            src="https://dl.espressif.com/dl/audio/ff-16b-2c-44100hz.mp3"
            onTimeUpdate={(e) => this.onTimeUpdateHandler}
        >
            Your browser does not support the
  <code>audio</code> element.
</audio>
        )
    }
}

export default Player;
