import React from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import QueueMusicIcon from '@material-ui/icons/QueueMusic';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import styles from './MusicCollection.module.css'; 

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
  handleCallback = (songName, songUrl) =>{
    this.props.parentCallback(songName, songUrl);
  }
   render(){ 
    const playLists = this.props.collection;
     return (
     <div className={styles.box}>
     <h1>Playlist</h1>
     <hr className="dashed"></hr>
     <div>
     <MusicPlayerView playLists = {playLists} parentCallback = {this.handleCallback}>
     </MusicPlayerView>
     </div>
     </div>
     )
   }
}


class MusicPlayerView extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      items: Array(this.props.playLists.length).fill(false),
    };
  }

  addItem(index){
    let tmpItems = this.state.items.slice();
    tmpItems[index] = !tmpItems[index];
    this.setState({items: tmpItems});
  }


  render(){
  const songs = (songName, songUrl) => {
    this.props.parentCallback(songName, songUrl);
    }
  const playLists = this.props.playLists.map((playlist, index) => {
    const myArray = new Array();
    myArray.push('Bes Song ever');
    myArray.push('Just something');
    return (
      <List key = {index} >
        <ListItem 
          button onClick={() => this.addItem(index)}
          className={styles.icon}
          >
        <ListItemIcon className={styles.icon}>
          <QueueMusicIcon className={styles.icon}/>
        </ListItemIcon>
        <ListItemText 
        disableTypography
        primary= {<div className = {styles.text}>{playlist.name}</div>}
        />
        {this.state.items[index] ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={this.state.items[index]} timeout="auto" unmountOnExit>
       {playlist.songs.map(function(item) {
          return (
          <ListItem key = {item.songName} button onClick={() => songs(item.songName, item.songUrl)} className = {styles.collection}>
            <ListItemText 
            disableTypography
            primary= {<div className = {styles.itemtext}>{item.songName}</div>}/>
          </ListItem>)
       })}
      </Collapse>
      </List>
    )
  });

  return (
    <div>
    <ol>{playLists}</ol>
    </div>
  )
  }
}

export default MusicCollection
