import React, { Component } from 'react';
import './App.css';
import MusicCollection from './components/MusicCollection.js';

 class App extends React.Component {
   render(){ 
     return (
     <MusicPlayerView>
     </MusicPlayerView>
     )
   }
}

class MusicPlayerView extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return (
    <div>
      <MusicLibrary></MusicLibrary>
      <SoundTrackPlayer></SoundTrackPlayer>
      <ControlPanel></ControlPanel>
      <UploadView></UploadView>
    </div> )
  }
}

class MusicLibrary extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      value: '',
      collection: Array(),
      myValue: 'jij',
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
    //const playlists = this.state.collection.slice();
    //playlists.push(this.state.value);
    //this.setState((state) => {
    //  return {collection: playlists};
    //});
  }

  handleSubmit(event) {
    const playlists = this.state.collection.slice();
    playlists.push(this.state.value);
    this.setState((state) => {
      return {collection: playlists};
    });
    event.preventDefault();
  }

  render(){
    const myCollection = this.state.collection;
    return (
      <div>
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <div><MusicCollection collection = {myCollection}></MusicCollection></div>
      </div>
    )

  }

  componentDidUpdate(prevProps, prevState, snapshot){
    console.log(this.state.collection);
    console.log('value is: ' + this.state.value);
  }
}

class SoundTrackPlayer extends React.Component{
  render(){
    return <div>SoundTrackPlayer</div>
  }
}

class ControlPanel extends React.Component{
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component{
  render() {
    return <div>UploadView</div>
  }
}


export default App;
