    }
    this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    this.fetchFrequency = this.fetchFrequency.bind(this);
  }

  fetchFrequency = async (value, currentTimeMs) => {
    var myvalue = await value;
    console.log('value: ' + myvalue[Math.round(currentTimeMs)]);
    this.props.soundTrackPlayerCallback(myvalue[Math.round(currentTimeMs)]);
