  original = Promise.resolve(drawAudio('https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3'));
  onTimeUpdateHandler = () => {
    const cast = Promise.resolve(this.original);
    var audio = document.getElementById('track');
    var currentTimeMs = audio.currentTime * 10;
    this.setState({ time: Math.round(currentTimeMs) })

    console.log(Math.round(currentTimeMs));
    cast.then((value) => this.fetchFrequency(value, currentTimeMs));
  }

  render() {
