import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Select from 'react-select'
import TextField from '@material-ui/core/TextField';

class UploadDialog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      songName: '',
      songUrl: '',
      playlist: null,
    };
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleUrlChange = this.handleUrlChange.bind(this);
    this.handlePlayListChange = this.handlePlayListChange.bind(this);
    this.handleDialogOpen = this.handleDialogOpen.bind(this);
    this.handleDialogClose = this.handleDialogClose.bind(this);
    this.handleDialogSubmit = this.handleDialogSubmit.bind(this);
  }

  handleNameChange(event) {
    this.setState({ songName: event.target.value });
  };

  handleUrlChange(event) {
    this.setState({ songUrl: event.target.value });
  };

  handlePlayListChange(e) {
    this.setState({ playlist: e.value });
  }

  handleDialogOpen = () => {
    this.setState({ open: true });
  };

  handleDialogClose = () => {
    this.setState({ 
      open: false, 
      songName: '', 
      songUrl: '' });
  };

  handleDialogSubmit = () => {

   // this.state.playlist ? {musicCollection = [...musicCollection, ]}

    //let musicCollection = this.props.collection.slice();
    //musicCollection[this.state.playlist].songs.push({ songName: this.state.songName, songUrl: this.state.songUrl });
    //this.props.parentCallback(musicCollection);

    this.setState({ 
      open: false, 
      songName: '', 
      songUrl: '' });
  };

  render() {

    const playlists = this.props.collection.map((playlist, index) => {
      return (
        { label: playlist.name, value: index }
      )
    });

    return (
      <React.Fragment>
        <Button variant="outlined" color="primary" onClick={this.handleDialogOpen}>
          Upload your song
      </Button>
        <Dialog
          open={this.state.open}
          onClose={handleDialogClose}>
          <DialogTitle>Upload a Song</DialogTitle>
          <DialogContent>
            <DialogContentText>
              You can choose the playlist. If you don't have a playlist yet, then go back to the page and create one.
          </DialogContentText>

            <Select
              options={playlists}
              onChange={(e) => { this.handlePlayListChange(e) }}
            />

            <br></br>

            <DialogContentText>
              You can now add a song to your playlist. Give your song a name and then add the url link to your song.
          </DialogContentText>

            <TextField
              value={this.state.songName}
              onChange={this.handleNameChange}
              autoFocus
              margin="dense"
              label="Song name"
              fullWidth
            />
            <TextField
              value={this.state.songUrl}
              onChange={this.handleUrlChange}
              autoFocus
              margin="dense"
              label="Song URL"
              fullWidth
            />
          </DialogContent>

          <DialogActions>
            <Button onClick={handleDialogClose} color="primary">
              Close
          </Button>
            <Button onClick={handleDialogSubmit} color="primary">
              Submit
          </Button>
          </DialogActions>
        </Dialog>
      </React.Fragment>
    );
  }
}

export default UploadDialog
