import React from 'react';
import MusicCollection from './components/MusicCollection.js';
import UploadDialog from './components/UploadDialog.js';
import './App.css';

 class App extends React.Component {
   render(){ 
     return (
     <MusicPlayerView>
     </MusicPlayerView>
     )
   }
}

class MusicPlayerView extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      collection: Array(),
      currentSong: '',
      currentUrl: '',
    }
  }

  handleCallback = (childData) =>{
    this.setState({collection: childData});
    this.forceUpdate();
  }

  render(){
    console.log(this.state.collection)
    return (
    <div>
      <MusicLibrary collection = {this.state.collection} parentCallback = {this.handleCallback}></MusicLibrary>
      <SoundTrackPlayer></SoundTrackPlayer>
      <ControlPanel></ControlPanel>
      <UploadView></UploadView>
    </div> )
  }
}

class MusicLibrary extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      value: '',
      myCollection: this.props.collection,
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCallback = this.handleCallback.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    const playlists = this.props.collection.slice();
    playlists.push({name: this.state.value, songs: []});
    this.setState({value: ''});
    this.props.parentCallback(playlists);
    event.preventDefault();
  }

  handleCallback = (childData) =>{
    this.setState({myCollection: childData});
    this.forceUpdate();
  }

  render(){
    const myCollection = this.props.collection;
    return (
      <div>
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <div><UploadDialog collection = {myCollection} parentCallback = {this.handleCallback}></UploadDialog></div>
      <div><MusicCollection collection = {myCollection}></MusicCollection></div>
      </div>
    )

  }
}

class SoundTrackPlayer extends React.Component{
  render(){
    return <div>SoundTrackPlayer</div>
  }
}

class ControlPanel extends React.Component{
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component{
  render() {
    return <div>UploadView</div>
  }
}


export default App;
