import React from 'react';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import AppBar from '@material-ui/core/AppBar';
import {
    BrowserRouter as Router,
    Switch,
    Route
} from "react-router-dom";

import PrivateRoute from '../../utils/PrivateRoute';

import MusicCollection from '../MusicCollection/MusicCollection';
import RegistrationForm from '../Dialogs/RegistrationForm';
import LoginForm from '../Dialogs/LoginForm';

class UserMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            anchorEl: null | HTMLElement,
            isOpen: false, 
            isLoggedIn: false,
            playlistCollection: [],
        }
        this.onSelection = this.onSelection.bind(this);
    }

    openMenu = (event) => {
        this.setState({ anchorEl: event.currentTarget, isOpen: true });
    };

    closeMenu = () => {
        this.setState({
            anchorEl: null,
            isOpen: false
        })
        if(this.state.isLoggedIn){
            this.props.history.push('/login');
        }
    }

    onSelection(){
        this.setState({
            anchorEl: null,
            isOpen: false
        })
        this.state.history.push('/loggedIn');
    }

    render() {
        return (
            <AppBar position="static">
                <Toolbar>
                    <IconButton edge="start" color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton>
                    <div>
                        <IconButton
                            aria-label="account of current user"
                            aria-controls="menu-appbar"
                            aria-haspopup="true"
                            onClick={this.openMenu}
                            color="inherit"
                        >
                            <AccountCircle />
                        </IconButton>
                        <Menu
                            id="menu-appbar"
                            anchorEl={this.state.anchorEl}
                            anchorOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            keepMounted
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            open={this.state.isOpen}
                            onClose={this.closeMenu}
                        >
                            <MenuItem onClick={this.onSelection}>{this.state.isLoggedIn ? 'Logout' : 'Login'}</MenuItem>
                        </Menu>
                    </div>
                </Toolbar>
                <Router>
                <div>
                    <Switch>
                        <Route path="/register">
                            <RegistrationForm
                            />
                        </Route>
                        <Route path="/login">
                            <LoginForm
                            />
                        </Route>
                        <PrivateRoute path="/loggedIn">
                            <MusicCollection playlistCollection={this.state.playlistCollection}></MusicCollection>
                        </PrivateRoute>
                    </Switch>
                </div>
            </Router>
            </AppBar>
        );
    }
}

export default UserMenu;
