import React from 'react';
import './Player.css';
import { API_BASE_URL } from '../../constants/apiConstants'

//https://developer.mozilla.org/en-US/docs/Web/API/AnalyserNode/getFloatFrequencyData
class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.analyserNode = null;
        this.dataArray = null;
        this.bufferLength = null;
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
        this.onAudioLoad = this.onAudioLoad.bind(this);
    }

    componentDidMount() {
        this.canvas = document.getElementById('visualizer');
        this.ctx = this.canvas.getContext("2d");

        this.centerX = this.canvas.width / 2;
        this.centerY = this.canvas.height / 2;
        this.radius = document.body.clientWidth <= 425 ? 120 : 160;
        this.steps = document.body.clientWidth <= 425 ? 60 : 120;
        this.interval = 360 / this.steps;
        this.pointsUp = [];
        this.pointsDown = [];
        this.running = false;
        this.pCircle = 2 * Math.PI * this.radius;
        this.angleExtra = 90;

        // Create points
        for (let angle = 0; angle < 360; angle += this.interval) {
            let distUp = 1.1;
            let distDown = 0.9;

            this.pointsUp.push({
                angle: angle + this.angleExtra,
                x: this.centerX + this.radius * Math.cos((-angle + this.angleExtra) * Math.PI / 180) * distUp,
                y: this.centerY + this.radius * Math.sin((-angle + this.angleExtra) * Math.PI / 180) * distUp,
                dist: distUp
            });

            this.pointsDown.push({
                angle: angle + this.angleExtra + 5,
                x: this.centerX + this.radius * Math.cos((-angle + this.angleExtra + 5) * Math.PI / 180) * distDown,
                y: this.centerY + this.radius * Math.sin((-angle + this.angleExtra + 5) * Math.PI / 180) * distDown,
                dist: distDown
            });
        }
        this.onAudioLoad();
        this.onTimeUpdateHandler();
    }

    drawLine(points) {
        let origin = points[0];

        this.ctx.beginPath();
        this.ctx.strokeStyle = 'rgba(255,255,255,0.5)';
        this.ctx.lineJoin = 'round';
        this.ctx.moveTo(origin.x, origin.y);

        for (let i = 0; i < points.length; i++) {
            this.ctx.lineTo(points[i].x, points[i].y);
        }

        this.ctxctx.lineTo(origin.x, origin.y);
        this.ctxctx.stroke();
    }

    connectPoints(pointsA, pointsB) {
        for (let i = 0; i < pointsA.length; i++) {
            this.ctx.beginPath();
            this.ctx.strokeStyle = 'rgba(255,255,255,0.5)';
            this.ctx.moveTo(pointsA[i].x, pointsA[i].y);
            this.ctx.lineTo(pointsB[i].x, pointsB[i].y);
            this.ctx.stroke();
        }
    }

    update(dt) {
        let audioIndex, audioValue;

        // get the current audio data
        this.analyserL.getByteFrequencyData(this.audioDataArrayL);
        this.analyserR.getByteFrequencyData(this.audioDataArrayR);

        for (let i = 0; i < this.pointsUp.length; i++) {
            audioIndex = Math.ceil(this.pointsUp[i].angle * (this.bufferLengthL / (this.pCircle * 2))) | 0;
            // get the audio data and make it go from 0 to 1
            audioValue = this.audioDataArrayL[audioIndex] / 255;

            this.pointsUp[i].dist = 1.1 + audioValue * 0.8;
            this.pointsUp[i].x = this.centerX + this.radius * Math.cos(-this.pointsUp[i].angle * Math.PI / 180) * this.pointsUp[i].dist;
            this.pointsUp[i].y = this.centerY + this.radius * Math.sin(-this.pointsUp[i].angle * Math.PI / 180) * this.pointsUp[i].dist;

            audioIndex = Math.ceil(this.pointsDown[i].angle * (this.bufferLengthR / (this.pCircle * 2))) | 0;
            // get the audio data and make it go from 0 to 1
            audioValue = this.audioDataArrayR[audioIndex] / 255;

            this.pointsDown[i].dist = 0.9 + audioValue * 0.2;
            this.pointsDown[i].x = this.centerX + this.radius * Math.cos(-this.pointsDown[i].angle * Math.PI / 180) * this.pointsDown[i].dist;
            this.pointsDown[i].y = this.centerY + this.radius * Math.sin(-this.pointsDown[i].angle * Math.PI / 180) * this.pointsDown[i].dist;
        }
    }

    onAudioLoad = () => {
        try {
            const audioCtx = new AudioContext();
            var audio = document.getElementById('track');
            const audioSourceNode = audioCtx.createMediaElementSource(audio);

            this.analyserNode = audioCtx.createAnalyser();
            this.analyserNode.fftSize = 1024;
            this.bufferLength = this.analyserNode.frequencyBinCount;
            this.dataArray = new Float32Array(this.bufferLength);

            audioSourceNode.connect(this.analyserNode);
            this.analyserNode.connect(audioCtx.destination);
        } catch (error) {
        }
    }

    onTimeUpdateHandler = () => {
        try {
            //Schedule next redraw
            requestAnimationFrame(this.onTimeUpdateHandler);

            //Get spectrum data
            this.analyserNode.getFloatFrequencyData(this.dataArray);

           this.ctx.fillStyle = 'rgb(255, 255, 255)';
            this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

           this.update(this.dt);


           this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

           this.drawLine(this.pointsUp);
           this.drawLine(this.pointsDown);
           this.connectPoints(this.pointsUp, this.pointsDown);
        } catch (error) {
        }
    }

    render() {
        if (this.props.currentSongID) {
            return (
                <div className="player">
                    <figcaption id="caption">{this.props.currentSongName}</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src={API_BASE_URL + '/media/getSongFile?songID=' + this.props.currentSongID}
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        } else {
            return (
                <div className="player">
                    <figcaption id="caption">Test Song</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src="https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3"
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        }
    }
}

export default Player;
