import React from 'react';

import './App.css';
import MusicCollection from './components/MusicCollection.js';
import UploadDialog from './components/UploadDialog.js';
import drawAudio from './components/AudioAnalyzer.js';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import DialogTitle from '@material-ui/core/DialogTitle';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import injectSheet, { ThemeProvider } from 'react-jss';
import Dialog from '@material-ui/core/Dialog';
import RegistrationForm from './components/RegistrationForm';
import AlertComponent from './components/AlertComponent';
import LoginForm from './components/LoginForm';
import PrivateRoute from './utils/PrivateRoute';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import { ACCESS_TOKEN_NAME } from './constants/apiConstants';

const styles = () => ({
  wrapper: {
    opacity: props => props.opacity,
    backgroundImage: "url('https://nypost.com/wp-content/uploads/sites/2/2019/11/universe-in-giant-loop.jpg?quality=90&strip=all&w=1286')",
    height: 700,
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
  }
})

const Comp = ({ classes }) => (
  <div className={classes.wrapper}>
  </div>
)

const StyledComp = injectSheet(styles)(Comp)
const theme = {
  background: "yellow",
  color: '#24292e'
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFrequence: 1,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.setState({ currentFrequence: currentFrequence })
  }
  render() {
    console.log(this.state.currentFrequence)
    return (
      <div>
        <ThemeProvider theme={theme}>
          <StyledComp opacity={this.state.currentFrequence} />
        </ThemeProvider>
        <MusicPlayerView soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </MusicPlayerView>
      </div>
    )
  }
}

class MusicPlayerView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collection: Array(),
      currentSong: '',
      currentUrl: '',
      open: true,
      isDialogOpen: false,
      showError: '',
      errorMessage: null,
      title: 'Register',
      openAccountMenu: false,
      anchorEl: null | HTMLElement,
      auth: true,
      isLoggedIn: false,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.setState({ collection: childData });
  }

  handleCallbackFromMusicCollection = (songName, songUrl) => {
    this.setState({ currentSong: songName });
    this.setState({ currentUrl: songUrl });
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.props.soundTrackPlayerCallback(currentFrequence);
  }

  handleRegistrationFormCallback = (errorMessage) => {
    this.setState({ errorMessage: errorMessage })
  }

  handleClickOpen = () => {
    this.setState({ isDialogOpen: true });
    console.log("dialog is now open");
  };

  handleClose = () => {
    this.setState({ isDialogOpen: false });
  };

  updateTitle = (title) => {
    this.setState({ title: title });
  }

  handleMenu = (event) => {
    this.setState({ anchorEl: event.currentTarget, openAccountMenu: true });
  };

  handleCloseAccoungMenu = () => {
    this.setState({
      anchorEl: null,
      openAccountMenu: false
    })
    if (localStorage.getItem(ACCESS_TOKEN_NAME) != null) {
      localStorage.removeItem(ACCESS_TOKEN_NAME)
      props.history.push('/login')
    } else {
      this.handleClickOpen;
    }
  }

  handleChange = (event) => {
    this.setState({ auth: event.target.checked });
  };

  render() {
    console.log(localStorage.getItem(ACCESS_TOKEN_NAME))
    return (
      <div>
        <div className="root">
          <div className={this.state.open ? "slider-show" : "slider-hide"}>
            <AppBar position="static">
              <Toolbar>
                <IconButton edge="start" color="inherit" aria-label="menu">
                  <MenuIcon />
                </IconButton>

                <FormGroup>
                  <FormControlLabel
                    control={<Router><Switch checked={this.state.auth} onChange={this.handleChange} aria-label="login switch" /></Router>}
                    label={this.state.auth ? 'Logout' : 'Login'}
                  />
                </FormGroup>
                {this.state.auth && (
                  <div>
                    <IconButton
                      aria-label="account of current user"
                      aria-controls="menu-appbar"
                      aria-haspopup="true"
                      onClick={this.handleMenu}
                      color="inherit"
                    >
                      <AccountCircle />
                    </IconButton>
                    <Menu
                      id="menu-appbar"
                      anchorEl={this.state.anchorEl}
                      anchorOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                      }}
                      keepMounted
                      transformOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                      }}
                      open={this.state.openAccountMenu}
                      onClose={this.handleCloseAccoungMenu}
                    >
                      <MenuItem onClick={this.handleCloseAccoungMenu}>{localStorage.getItem(ACCESS_TOKEN_NAME) ? 'Logout' : 'Login'}</MenuItem>
                    </Menu>
                  </div>
                )}
                <Button color="inherit" onClick={this.handleClickOpen}>Login</Button>
              </Toolbar>
            </AppBar>
            <Router>
              <div className="App">
                <div className="container d-flex align-items-center flex-column">
                  <Switch>
                    <Route path="/" exact={true}>
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>

                    <Route path="/register">
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>
                    <Route path="/login">
                      <Dialog
                        open={this.state.isDialogOpen}
                        onClose={this.handleClose}>
                        <DialogTitle>{this.state.title}</DialogTitle>
                        <LoginForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                      </Dialog>
                    </Route>
                    <PrivateRoute path="/loggedIn">
                      <MusicLibrary
                        collection={this.state.collection}
                        currentSong={this.state.currentSong}
                        currentUrl={this.state.currentUrl}
                        uploadDialogCallback={this.handleCallbackFromUploadDialog}
                        musicCollectionCallback={this.handleCallbackFromMusicCollection}>
                      </MusicLibrary>
                    </PrivateRoute>
                  </Switch>
                  <AlertComponent errorMessage={this.state.errorMessage} hideError={() => {
                    this.setState({ errorMessage: null });
                  }} />
                </div>
              </div>
              <div className="button">
                {this.state.open
                  ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
                  : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
                }
              </div>
            </Router>
          </div>
        </div>
        <SoundTrackPlayer
          currentSong={this.state.currentSong}
          currentUrl={this.state.currentUrl}
          soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </SoundTrackPlayer>
      </div>)
  }
}

class MusicLibrary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCallbackFromUploadDialog = this.handleCallbackFromUploadDialog.bind(this);
    this.handleCallbackFromMusicCollection = this.handleCallbackFromMusicCollection.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    const playlists = this.props.collection.slice();
    playlists.push({ name: this.state.value, songs: [] });
    this.setState({ value: '' });
    this.props.uploadDialogCallback(playlists);
    event.preventDefault();
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.props.uploadDialogCallback(childData);
  }
  handleCallbackFromMusicCollection = (songName, songUrl) => {
    this.props.musicCollectionCallback(songName, songUrl);
  }

  render() {
    return (
      <div>
        <div className="UploadDialog">
          <p>Upload your songs here</p>
          <UploadDialog
            collection={this.props.collection}
            parentCallback={this.handleCallbackFromUploadDialog}>
          </UploadDialog>
        </div>
        <div className="Library">
          <div >
            <MusicCollection
              collection={this.props.collection}
              parentCallback={this.handleCallbackFromMusicCollection}>
            </MusicCollection>
          </div>
          <form onSubmit={this.handleSubmit} className="Form">
            <p>Create a new playlist</p>
            <label>
              name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
        </div>
      </div>
    )

  }
}

