import React from 'react';
import drawAudio from './AudioAnalyzer.js';
import { API_BASE_URL, ACCESS_TOKEN_NAME } from '../constants/apiConstants';
import axios from 'axios';

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
        this.onPlayHandler = this.onPlayHandler.bind(this);
    }

    componentDidMount() {
        this.audioData = null;
    }

    onTimeUpdateHandler = () => {
        // get current timestamp from audio
        var audio = document.getElementById('track');
        var timestamp = audio.currentTime * 10;
        var stream = audio.captureStream();
        console.log(stream);
        // resolve the current frequence and update parent
        this.audioData.then((value) => this.props.soundTrackPlayerCallback(value[Math.round(timestamp)]));
    }

    onPlayHandler(){
        this.audioData = Promise.resolve(drawAudio(API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID));
    }

    render() {
          return (
            <div className="Player">
                <figcaption>{this.props.currentSongID}</figcaption>
                <audio
                    id="track"
                    controls
                    onPlay={(e) => this.onPlayHandler(e)}
                    src={API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID}
                    onTimeUpdate={(e) => this.onTimeUpdateHandler(e)}
                >
                    Your browser does not support the
              <code>audio</code> element.
          </audio>
            </div>
        )
    }
}

export default Player;
