import React from 'react';

import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import UploadDialog from '../Dialogs/UploadDialog';
import Song from './Song';

import axios from 'axios';
import { API_BASE_URL, ACCESS_TOKEN_NAME } from '../../constants/apiConstants';


class Playlist extends React.Component {
    constructor(props) {
        super(props);
        this.handleUpload = this.handleUpload.bind(this);
    }

    async addSongPost(playlistName, songName, songUrl) {
        return axios.post(API_BASE_URL + '/media/addSong', { playlistName: playlistName, songName: songName, songUrl: songUrl }, { headers: { "auth-token": localStorage.getItem(ACCESS_TOKEN_NAME) } })
            .then(function (response) {
                if (response.status === 200) {
                    return response.data.affected;
                }
            }
            )
            .catch(function (error) {
                console.log(error);
            })
    }

    handleUpload(songName, songUrl) {
        Promise.resolve(this.addSongPost(this.props.playlistName, songName, songUrl)).then((result) => this.props.parentCallback(result));
    }


    onSelect = (songUrl) => {
        console.log('the song url is: ' + songUrl);
    }

    render() {
        return (
            <div>
                <Accordion>
                    <Card>
                        <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                {this.props.playlistName}
                            </Accordion.Toggle>
                            <UploadDialog parentCallback={this.handleUpload}>
                            </UploadDialog>
                        </Card.Header>
                        {this.props.songCollection.map((song, index) => {
                            return (
                                <Accordion.Collapse eventKey="0" key={index}>
                                    <Card key={index}>
                                        <Song songName={song.songName} songUrl={song.songUrl} parentCallback={this.props.parentCallback}></Song>
                                    </Card>
                                </Accordion.Collapse>
                            )
                        })}
                    </Card>
                </Accordion>
            </div>
        )
    }
}

export default Playlist;
