import React, { Component } from 'react';
import './App.css';
import MusicCollection from './components/MusicCollection.js';

 class App extends React.Component {
   render(){ 
     return (
     <MusicPlayerView>
     </MusicPlayerView>
     )
   }
}

class MusicPlayerView extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      collection: new Array(),
    }
    this.state.collection.push('Reaggy');
    this.state.collection.push('Classic');
    this.state.collection.push('Dance Music');
    this.state.collection.push('Freaky Music');
    this.state.collection.push('Relaxing Music');

  }
  render(){
    return (
    <div>
      <MusicLibrary collection = {this.state.collection}></MusicLibrary>
      <SoundTrackPlayer></SoundTrackPlayer>
      <ControlPanel></ControlPanel>
      <UploadView></UploadView>
    </div> )
  }
}

class MusicLibrary extends React.Component{
  constructor(props){
    super(props);
    this.state = {value: ''};
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render(){
    return (
      <div><MusicCollection collection = {this.props.collection}></MusicCollection></div>,
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    )

  }
}

class SoundTrackPlayer extends React.Component{
  render(){
    return <div>SoundTrackPlayer</div>
  }
}

class ControlPanel extends React.Component{
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component{
  render() {
    return <div>UploadView</div>
  }
}


export default App;
