import React from 'react';
import './Player.css';
import { API_BASE_URL } from '../../constants/apiConstants'

//https://developer.mozilla.org/en-US/docs/Web/API/AnalyserNode/getFloatFrequencyData
class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.analyserNode = null;
        this.dataArray = null;
        this.bufferLength = null;
        this.rotateIndex = 0;
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
        this.onAudioLoad = this.onAudioLoad.bind(this);
    }

    componentDidMount() {
        this.canvas = document.getElementById('visualizer');
        this.canvasCtx = this.canvas.getContext('2d');
        this.onAudioLoad();
        this.onTimeUpdateHandler();
    }

    onAudioLoad = () => {
        try {
            const audioCtx = new AudioContext();
            var audio = document.getElementById('track');
            const audioSourceNode = audioCtx.createMediaElementSource(audio);

            this.analyserNode = audioCtx.createAnalyser();
            this.analyserNode.fftSize = 1024;
            this.bufferLength = this.analyserNode.frequencyBinCount;
            this.dataArray = new Float32Array(this.bufferLength);

            audioSourceNode.connect(this.analyserNode);
            this.analyserNode.connect(audioCtx.destination);
        } catch (error) {
        }
    }

    onTimeUpdateHandler = () => {

        //Schedule next redraw
        requestAnimationFrame(this.onTimeUpdateHandler);

        //Get spectrum data
        this.analyserNode.getFloatFrequencyData(this.dataArray);

        //Draw black background
        this.canvasCtx.fillStyle = 'rgb(255, 255, 255)';
        this.canvasCtx.fillRect(0, 0, this.canvas.width, this.canvas.height);

        rotateIndex += .4;
        d3.select('.tiles')
            .style('transform', function () {
                return 'perspective(1000px) rotateX(66deg) rotateZ(' + rotateIndex + 'deg)';
            })
            .selectAll('div')
            .data(data)
            .style('transform', function (h) {
                return 'translateZ(' + ((h / 255) * 100) + 'px)';
            })
            .style('opacity', function (o) {
                return (o / 255);
            })
            .enter().append('div');
    }

    render() {
        if (this.props.currentSongID) {
            return (
                <div className="player">
                    <figcaption id="caption">{this.props.currentSongName}</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src={API_BASE_URL + '/media/getSongFile?songID=' + this.props.currentSongID}
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        } else {
            return (
                <div className="player">
                    <figcaption id="caption">Test Song</figcaption>
                    <audio
                        id="track"
                        controls
                        crossorigin="anonymous"
                        src="https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3"
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
                </div>
            )
        }
    }
}

export default Player;
