import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import styles from './MusicCollection.module.css'; 

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
  handleCallback = (childData) =>{
    this.props.parentCallback(childData);
  }
   render(){ 
    const playLists = this.props.collection;
     return (
     <div className={styles.library}>
     <div>Playlist</div>
     <MusicPlayerView playLists = {playLists} parentCallback = {this.handleCallback} className={styles.container}>
     </MusicPlayerView>
     </div>
     )
   }
}


class MusicPlayerView extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      items: Array(this.props.playLists.length).fill(false),
    };
  }

  addItem(index){
    let tmpItems = this.state.items.slice();
    tmpItems[index] = !tmpItems[index];
    this.setState({items: tmpItems});
  }


  render(){
  const songs = (songName, songUrl) => {
    this.props.parentCallback(songUrl);
    }
  const playLists = this.props.playLists.map((playlist, index) => {
    const myArray = new Array();
    myArray.push('Bes Song ever');
    myArray.push('Just something');
    return (
      <List key = {index}>
        <ListItem button onClick={() => this.addItem(index)}
          className={styles.nested}>
        <ListItemIcon>
          <DoubleArrowIcon />
        </ListItemIcon>
        <ListItemText primary= {playlist.name} />
        {this.state.items[index] ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={this.state.items[index]} timeout="auto" unmountOnExit>
       {playlist.songs.map(function(item) {
          return (
          <ListItem key = {item.songName} button onClick={() => songs(item.songName, item.songUrl)}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary= {item.songName} />
          </ListItem>)
       })}
      </Collapse>
      </List>
    )
  });

  return (
    <div>
    <ol>{playLists}</ol></div>

  )
  }
}

export default MusicCollection
