import React from 'react';
import './App.css';

import MusicCollection from './components/MusicCollection.js';
import UploadDialog from './components/UploadDialog.js';

class App extends React.Component {
  render() {
    return (
      <MusicPlayerView>
      </MusicPlayerView>
    )
  }
}

class MusicPlayerView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collection: Array(),
      currentSong: '',
      currentUrl: '',
    }
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.setState({ collection: childData });
  }

  handleCallbackFromMusicCollection = (childData) => {
    this.setState({ currentUrl: childData });
  }

  render() {
    return (
      <div>
        <div className="MusicPlayerView">
          <MusicLibrary
            collection={this.state.collection}
            currentSong={this.state.currentSong}
            currentUrl={this.state.currentUrl}
            uploadDialogCallback={this.handleCallbackFromUploadDialog}
            musicCollectionCallback={this.handleCallbackFromMusicCollection}>
          </MusicLibrary>
        </div>
        <SoundTrackPlayer
          currentSong={this.state.currentSong}
          currentUrl={this.state.currentUrl}>
        </SoundTrackPlayer>
        <ControlPanel></ControlPanel>
        <UploadView></UploadView>
      </div>)
  }
}

class MusicLibrary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCallbackFromUploadDialog = this.handleCallbackFromUploadDialog.bind(this);
    this.handleCallbackFromMusicCollection = this.handleCallbackFromMusicCollection.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    const playlists = this.props.collection.slice();
    playlists.push({ name: this.state.value, songs: [] });
    this.setState({ value: '' });
    this.props.uploadDialogCallback(playlists);
    event.preventDefault();
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.props.uploadDialogCallback(childData);
  }
  handleCallbackFromMusicCollection = (childData) => {
    this.props.musicCollectionCallback(childData);
  }

  render() {
    return (
      <div>
        <div><UploadDialog collection={this.props.collection} parentCallback={this.handleCallbackFromUploadDialog}></UploadDialog></div>
        <div><MusicCollection collection={this.props.collection} parentCallback={this.handleCallbackFromMusicCollection}></MusicCollection></div>
        <form onSubmit={this.handleSubmit}>
          <h2 className="h2">Create a new playlist</h2>
          <label>
            name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    )

  }
}

class SoundTrackPlayer extends React.Component {
  render() {
    return (
      <div>
        <figcaption>{this.props.currentSong}</figcaption>
        <audio
          controls
          src={this.props.currentUrl}>
          Your browser does not support the
          <code>audio</code> element.
      </audio>
      </div>
    )
  }
}

class ControlPanel extends React.Component {
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component {
  render() {
    return <div>UploadView</div>
  }
}


export default App;
