import React from 'react';
import './Player.css';
import { API_BASE_URL } from '../../constants/apiConstants';

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    }

    onTimeUpdateHandler = () => {
        const audioCtx = new AudioContext();
        var audio = document.getElementById('track');
        const audioSourceNode = audioCtx.createMediaElementSource(audio);

        const analyserNode = audioCtx.createAnalyser();
        analyserNode.fftSize = 256;
        const bufferLength = analyserNode.frequencyBinCount;
        const dataArray = new Float32Array(bufferLength);

        audioSourceNode.connect(analyserNode);
        analyserNode.connect(audioCtx.destination);


        const canvas = document.createElement('canvas');
        canvas.style.position = 'absolute';
        canvas.style.top = 0;
        canvas.style.left = 0;
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
        document.body.appendChild(canvas);
        const canvasCtx = canvas.getContext('2d');
        canvasCtx.clearRect(0, 0, canvas.width, canvas.height);

        var timestamp = audio.currentTime * 10;
        // resolve the current frequence and update parent
        this.audioData.then((value) => this.props.soundTrackPlayerCallback(value[Math.round(timestamp)]));
    }

    render() {
        return (
            <div className="player">
                <figcaption id="caption">{this.props.currentSongName}</figcaption>
                <audio
                    id="track"
                    controls
                    src={API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID}
                    onTimeUpdate={(e) => null}
                >
                    Your browser does not support the
              <code>audio</code> element.
          </audio>
            </div>
        )
    }
}

export default Player;
