import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';


class UploadDialog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      songName: '',
      songUrl: '',
    };
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleUrlChange = this.handleUrlChange.bind(this);
    this.handleDialogOpen = this.handleDialogOpen.bind(this);
    this.handleDialogClose = this.handleDialogClose.bind(this);
    this.handleDialogSubmit = this.handleDialogSubmit.bind(this);
  }

  handleNameChange(event) {
    this.setState({ songName: event.target.value });
  };

  handleUrlChange(event) {
    this.setState({ songUrl: event.target.value });
  };

  handleDialogOpen = () => {
    this.setState({ open: true });
  };

  handleDialogClose = () => {
    this.setState({ 
      open: false, 
      songName: '', 
      songUrl: '' });
  };

  handleDialogSubmit = () => {

    this.props.songlist.push({songName: this.state.songName, songUrl: this.state.songUrl});

    this.setState({ 
      open: false, 
      songName: '', 
      songUrl: '' });
  };

  render() {
    return (
      <React.Fragment>
        <Button variant="outlined" color="primary" onClick={this.handleDialogOpen}>
          ADD A SONG
      </Button>
        <Dialog
          open={this.state.open}
          onClose={this.handleDialogClose}>
          <DialogTitle>Upload a Song</DialogTitle>
          <DialogContent>
            <TextField
              value={this.state.songName}
              onChange={this.handleNameChange}
              autoFocus
              margin="dense"
              label="Song name"
              fullWidth
            />
            <TextField
              value={this.state.songUrl}
              onChange={this.handleUrlChange}
              autoFocus
              margin="dense"
              label="Song URL"
              fullWidth
            />
          </DialogContent>

          <DialogActions>
            <Button onClick={this.handleDialogClose} color="primary">
              Close
          </Button>
            <Button onClick={this.handleDialogSubmit} color="primary">
              Submit
          </Button>
          </DialogActions>
        </Dialog>
      </React.Fragment>
    );
  }
}

export default UploadDialog
