import React from 'react';

import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';

import UploadDialog from '../Dialogs/UploadDialog';
import Song from './Song';


class Playlist extends React.Component {
    constructor(props) {
        super(props);
        this.handleUpload = this.handleUpload.bind(this);
    }

    handleUpload(songName, songUrl) {
        //this.props.parentCallback(songName, songUrl);
    }

    onSelect = (songUrl) => {
        console.log('the song url is: ' + songUrl);
    }

    render() {
        return (
            <div>
                <Accordion>
                    <Card>
                        <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                {this.props.playlistName}
                            </Accordion.Toggle>
                            <UploadDialog songlist={this.props.songCollection}>
                            </UploadDialog>
                        </Card.Header>
                        {this.props.songCollection.map((song, index) => {
                            return (
                                <Accordion.Collapse eventKey="0" key={index}>
                                    <Card key={index}>
                                        <ListGroup.Item onClick={this.onSelect(song.songUrl)}>{song.songName}</ListGroup.Item>
                                    </Card>
                                </Accordion.Collapse>
                            )
                        })}
                    </Card>
                </Accordion>
            </div>
        )
    }
}

export default Playlist;
