import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from 'react-select'
import styles from './UploadDialog.module.css'; 
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  form: {
    display: 'flex',
    flexDirection: 'column',
    margin: 'auto',
    width: 'fit-content',
  },
  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 120,
  },
  formControlLabel: {
    marginTop: theme.spacing(1),
  },
}));

class UploadDialog extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      open: false,
      value: '',
      myurl: '',
      playlist: null,
    };
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleUrlChange = this.handleUrlChange.bind(this);
  }

  handleNameChange(event){
    this.setState({value: event.target.value});
  };

  handleUrlChange(event){
    this.setState({myurl: event.target.value});
  };

  handleMaxWidthChange(event) {
    this.setState({
      playlist: event.target.value,
    });
  }

  render(){

  const handleClickOpen = () => {
    this.setState({open: true});
  };

  const handleClose = () => {
    this.setState({open: false});
    this.setState({value: ''});
    this.setState({myurl: ''});
  };

  const handleSubmit = () => {
    this.setState({open: false});
    let myCollection2 = this.props.collection.slice();
    myCollection2[this.state.playlist].songs.push(this.state.value);
   // this.setState({value: ''});
   // this.setState({myurl: ''});
  };

  const playlists = this.props.collection.map((playlist, index) => {
    return(
    {label: playlist.name, value: index}
    )
  });

  const handleMaxWidthChange = (event) => {
    this.setState({
      playlist: event.target.value,
    });
  }

  return (
    <React.Fragment>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Upload your song
      </Button>
      <Dialog
        open={this.state.open}
        onClose={handleClose}
      >
        <DialogTitle>Upload a Song</DialogTitle>
        <DialogContent>
          <DialogContentText>
            You can choose the playlist. If you don't have a playlist yet, then go back to the page and create one.
          </DialogContentText>

          <Select 
          options={playlists} 
          className="basic-single"
          classNamePrefix="select"
          />

          <DialogContentText>
            You can now add a song to your playlist. Give your song a name and then add the url link to your song.
          </DialogContentText>
          <TextField 
            value={this.state.value} 
            onChange={this.handleNameChange}
            autoFocus
            margin="dense"
            label="Song name"
            fullWidth
          />
          <TextField
            value={this.state.myurl} 
            onChange={this.handleUrlChange}
            autoFocus
            margin="dense"
            label="Song URL"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Close
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
}

export default UploadDialog
