import React from 'react';
import ListGroup from 'react-bootstrap/ListGroup';

class MusicCollection extends React.Component {
    constructor(props) {
        super(props);
        this.state({ playlistName: "" })
    }

    handlePlaylistNameChange(event) {
        this.setState({ playlistName: event.target.value });
      }

    handleSubmit() {
        this.props.playlistCollection = [... this.state.playlistCollection, this.state.playlistName];
        this.setState({ playlistName: "" });
    }

    render() {
        console.log(this.props.playlistCollection)
        return (
            <div>
                <ListGroup>
                    {this.props.playlistCollection.map((Playlist) => {
                        return (
                            <Playlist songCollection={[]}></Playlist>
                        )
                    })}
                </ListGroup>
                <form onSubmit={this.handleSubmit} className="Form">
                    <p>Create a new playlist</p>
                    <label>
                        name:
          <input type="text" value={this.state.playlistName} onChange={this.handlePlaylistNameChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default MusicCollection;
