import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import styles from './MusicCollection.module.css'; 

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
   render(){ 
    const playLists = this.props.collection;
     return (
     <MusicPlayerView playLists = {playLists}>
     </MusicPlayerView>
     )
   }
}


class MusicPlayerView extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      items: Array(),
    };
  }
  render(){
  function addItem(e, index){
    e.preventDefault();
    let tmpItems = this.state.items;
    if(index > this.state.items.length) {
      tmpItems = Array(this.props.playLists.length).fill(false);
      tmpItems[index] = true;
      this.setState({items: tmpItems});
    } else {
    tmpItems[index] = !tmpItems[index];
    this.setState({items: tmpItems});
    }
  }

  const playLists = this.props.playLists.map((playlist, index) => {
    console.log('index is: '+index);
    return (
      <List key = {index} className={styles.root}>
        <ListItem button onClick={(e) => {
          addItem(e, index)
          } 
         }
          className={styles.nested}>
        <ListItemIcon>
          <DoubleArrowIcon />
        </ListItemIcon>
        <ListItemText primary= {playlist} />
        {this.state.items[index] ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={this.state.items[index]} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button onClick={() => {
            console.log('button pressed');
          }}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItem>
        </List>
      </Collapse>
      </List>
    )
  });
  return (
    <div>
    <ol>{playLists}</ol></div>

  )
  }
}

export default MusicCollection
