import React, { Component } from 'react';
import './App.css';
import MusicCollection from './components/MusicCollection.js';

 class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      value: '',
      collection: Array(),
    }
    this.state.collection.push('Reaggy');
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    const playlists = this.state.collection.slice();
    playlists.push(this.state.value);
    this.setState({collection: playlists});
  }
   render(){ 
     return (
       <div>
      <form onSubmit={this.handleSubmit}>
      <label>
        Name:
        <input type="text" value={this.state.value} onChange={this.handleChange} />
      </label>
      <input type="submit" value="Submit" />
    </form>
     <MusicPlayerView collection = {this.state.collection}>
     </MusicPlayerView>
     </div>
     )
   }
}

class MusicPlayerView extends React.Component{
  render(){
    return (
    <div>
      <MusicLibrary collection = {this.props.collection}></MusicLibrary>
      <SoundTrackPlayer></SoundTrackPlayer>
      <ControlPanel></ControlPanel>
      <UploadView></UploadView>
    </div> )
  }
}

class MusicLibrary extends React.Component{
  constructor(props){
    super(props);
    console.log(props);
  }

  render(){
    return (
      <div><MusicCollection collection = {this.props.collection}></MusicCollection></div>
    )

  }
}

class SoundTrackPlayer extends React.Component{
  render(){
    return <div>SoundTrackPlayer</div>
  }
}

class ControlPanel extends React.Component{
  render() {
    return <div>ControlPanel</div>
  }
}

class UploadView extends React.Component{
  render() {
    return <div>UploadView</div>
  }
}


export default App;
