import React from 'react';

import './App.css';
import MusicCollection from './components/MusicCollection.js';
import UploadDialog from './components/UploadDialog.js';
import drawAudio from './components/AudioAnalyzer.js';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFrequence: 1,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence){
    this.setState({currentFrequence: currentFrequence})
  }
  render() {
    console.log(this.state.currentFrequence)
    return (
      <div style={{'color': 'rgba(76, 175, 80, this.state.currentFrequence)'}}
      >
        <MusicPlayerView soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </MusicPlayerView>
      </div>
    )
  }
}

class MusicPlayerView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collection: Array(),
      currentSong: '',
      currentUrl: '',
      open: true,
    }
    this.handleCallbackFromSoundTrackPlayer = this.handleCallbackFromSoundTrackPlayer.bind(this);
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.setState({ collection: childData });
  }

  handleCallbackFromMusicCollection = (songName, songUrl) => {
    this.setState({ currentSong: songName });
    this.setState({ currentUrl: songUrl });
  }

  handleCallbackFromSoundTrackPlayer(currentFrequence) {
    this.props.soundTrackPlayerCallback(currentFrequence);
  }

  render() {
    return (
      <div>
        <div className="root">
          <div className={this.state.open ? "slider-show" : "slider-hide"}>
            <AppBar position="static">
              <Toolbar>
                <IconButton edge="start" color="inherit" aria-label="menu">
                  <MenuIcon />
                </IconButton>
                <Button color="inherit">Login</Button>
              </Toolbar>
            </AppBar>
            <div className="button">
              {this.state.open
                ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
                : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
              }
            </div>
            <div>
              <MusicLibrary
                collection={this.state.collection}
                currentSong={this.state.currentSong}
                currentUrl={this.state.currentUrl}
                uploadDialogCallback={this.handleCallbackFromUploadDialog}
                musicCollectionCallback={this.handleCallbackFromMusicCollection}>
              </MusicLibrary>
            </div>
          </div>
        </div>
        <SoundTrackPlayer
          currentSong={this.state.currentSong}
          currentUrl={this.state.currentUrl}
          soundTrackPlayerCallback={this.handleCallbackFromSoundTrackPlayer}>
        </SoundTrackPlayer>
      </div>)
  }
}

class MusicLibrary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCallbackFromUploadDialog = this.handleCallbackFromUploadDialog.bind(this);
    this.handleCallbackFromMusicCollection = this.handleCallbackFromMusicCollection.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    const playlists = this.props.collection.slice();
    playlists.push({ name: this.state.value, songs: [] });
    this.setState({ value: '' });
    this.props.uploadDialogCallback(playlists);
    event.preventDefault();
  }

  handleCallbackFromUploadDialog = (childData) => {
    this.props.uploadDialogCallback(childData);
  }
  handleCallbackFromMusicCollection = (songName, songUrl) => {
    this.props.musicCollectionCallback(songName, songUrl);
  }

  render() {
    return (
      <div>
        <div className="UploadDialog">
          <p>Upload your songs here</p>
          <UploadDialog
            collection={this.props.collection}
            parentCallback={this.handleCallbackFromUploadDialog}>
          </UploadDialog>
        </div>
        <div className="Library">
          <div >
            <MusicCollection
              collection={this.props.collection}
              parentCallback={this.handleCallbackFromMusicCollection}>
            </MusicCollection>
          </div>
          <form onSubmit={this.handleSubmit} className="Form">
            <p>Create a new playlist</p>
            <label>
              name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
        </div>
      </div>
    )

  }
}

class SoundTrackPlayer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
    }
    this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    this.fetchFrequency = this.fetchFrequency.bind(this);
  }

  fetchFrequency = async (value, currentTimeMs) => {
    var myvalue = await value;
    console.log('value: ' + myvalue[Math.round(currentTimeMs)]);
    this.props.soundTrackPlayerCallback(myvalue[Math.round(currentTimeMs)]);
  }

  original = Promise.resolve(drawAudio('https://s3-us-west-2.amazonaws.com/s.cdpn.io/3/shoptalk-clip.mp3'));
  onTimeUpdateHandler = () => {
    const cast = Promise.resolve(this.original);
    var audio = document.getElementById('track');
    var currentTimeMs = audio.currentTime * 10;
    this.setState({ time: Math.round(currentTimeMs) })

    console.log(Math.round(currentTimeMs));
    cast.then((value) => this.fetchFrequency(value, currentTimeMs));
  }

  render() {
    return (
      <div className="Player">
        <div className={this.state.open ? "player-show" : "player-hide"}>
          <figcaption>{this.props.currentSong}</figcaption>
          <audio
            id="track"
            controls
            src={this.props.currentUrl}
            onTimeUpdate={(e) => this.onTimeUpdateHandler(e)}
          >
            Your browser does not support the
          <code>audio</code> element.
      </audio>
          <div className="button2">
            {this.state.open
              ? <ChevronRightIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
              : <ChevronLeftIcon onClick={() => { this.setState({ open: !this.state.open }); }} />
            }
          </div>
        </div>
      </div>
    )
  }
}

export default App;
