import React from 'react';
import Dialog from '@material-ui/core/Dialog';
import RegistrationForm from './components/Dialogs/RegistrationForm';
import AlertComponent from './components/AlertComponent';
import LoginForm from './components/Dialogs/LoginForm';
import PrivateRoute from './utils/PrivateRoute';
import MusicCollection from './components/MusicCollection/MusicCollection';
import DialogTitle from '@material-ui/core/DialogTitle';
import {
    BrowserRouter as Router,
    Switch,
    Route
  } from "react-router-dom";



class UserLounge extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playlistCollection: [],
            title: 'Register',
            errorMessage: null,
            showError: '',
        }
        this.handleClose = this.handleClose.bind(this);
    }

    handleRegistrationFormCallback = (errorMessage) => {
        this.setState({ errorMessage: errorMessage })
    }

    handleClose = () => {
        this.props.parentCallback();
    };

    updateTitle = (title) => {
        this.setState({ title: title });
    }

    render() {
        return (
            <Router>
                <div className="App">
                    <div className="container d-flex align-items-center flex-column">
                        <Switch>
                            <Route path="/" exact={true}>
                                <Dialog
                                    open={this.props.isDialogOpen}
                                    onClose={this.handleClose}>
                                    <DialogTitle>{this.state.title}</DialogTitle>
                                    <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                                </Dialog>
                            </Route>
                            <Route path="/register">
                                <Dialog
                                    open={this.props.isDialogOpen}
                                    onClose={this.handleClose}>
                                    <DialogTitle>{this.state.title}</DialogTitle>
                                    <RegistrationForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                                </Dialog>
                            </Route>
                            <Route path="/login">
                                <Dialog
                                    open={this.props.isDialogOpen}
                                    onClose={this.handleClose}>
                                    <DialogTitle>{this.state.title}</DialogTitle>
                                    <LoginForm registrationFormCallback={this.handleRegistrationFormCallback} updateTitle={this.updateTitle} />
                                </Dialog>
                            </Route>
                            <PrivateRoute path="/loggedIn">
                                <MusicCollection playlistCollection={this.state.playlistCollection}></MusicCollection>
                            </PrivateRoute>
                        </Switch>
                        <AlertComponent errorMessage={this.state.errorMessage} hideError={() => {
                            this.setState({ errorMessage: null });
                        }} />
                    </div>
                </div>
            </Router>
        )
    }
}

export default UserLounge;
