import React from 'react';
import { API_BASE_URL } from '../constants/apiConstants';

class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
        }
        this.onTimeUpdateHandler = this.onTimeUpdateHandler.bind(this);
    }

    onTimeUpdateHandler = () => {
        // get current timestamp from audio
        var audio = document.getElementById('track');
        var timestamp = audio.currentTime * 10;
        // resolve the current frequence and update parent
        this.audioData.then((value) => this.props.soundTrackPlayerCallback(value[Math.round(timestamp)]));
    }

    render() {
        if(this.props.currentSongID){
        return (
            <div className="Player">
                <figcaption>{this.props.currentSongID}</figcaption>
                    <audio
                        id="track"
                        controls
                        src={API_BASE_URL + '/media/getSongFile/?songID=' + this.props.currentSongID}
                        onTimeUpdate={(e) => null}
                    >
                        Your browser does not support the
              <code>audio</code> element.
          </audio>
            </div>
        )
        }
    }
}

export default Player;
