import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
import styles from './MusicCollection.module.css'; 

class MusicCollection extends React.Component {
  constructor(props){
    super(props);
  }
   render(){ 
    const playLists = this.props.playLists;
     return (
     <MusicPlayerView playLists = {playLists}>
     </MusicPlayerView>
     )
   }
}


function MusicPlayerView(props) {
  const [items, setItems] = useState(props.playLists.map((playlist, index) => {return false}));
  const [isOpen, setOpen] = useState(false);

  function addItem(e, index){
    console.log(props.playLists.name);
    e.preventDefault();
    console.log('BP A.1 ' + items);
    console.log('BP B.1 ' + isOpen);
    console.log('BP C.1 ' + index);
    const tmpItems = items;
    console.log('BP A.2 ' + items);
    console.log('BP B.2 ' + isOpen);
    console.log('BP C.2 ' + index);
    setOpen(!items[index]);
    console.log('BP A.3 ' + items);
    console.log('BP B.3 ' + isOpen);
    console.log('BP C.3 ' + index);
    tmpItems[index] = isOpen;
    console.log('BP A.4 ' + items);
    console.log('BP B.4 ' + isOpen);
    console.log('BP C.4 ' + index);
    setItems(tmpItems);
    console.log('BP A.5 ' + items);
    console.log('BP B.5 ' + isOpen);
    console.log('BP C.5 ' + index);
  }

  const playLists = props.playLists.map((playlist, index) => {
    return (
      <List key = {index} className={styles.root}>
        <ListItem button onClick={(e) => {
          addItem(e, index)
          } 
         }
          className={styles.nested}>
        <ListItemIcon>
          <DoubleArrowIcon />
        </ListItemIcon>
        <ListItemText primary= {playlist} />
        {items[index] ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={items[index]} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button onClick={() => {
            console.log('button pressed');
          }}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItem>
        </List>
      </Collapse>
      </List>
    )
  });

  return (
    <div>
    <ol>{playLists}</ol></div>

  );
}

export default MusicCollection
